import { Component, OnInit } from '@angular/core';
@Component({
  selector: 'app-root',
  template: `
    <button label="change" [[ngClass]="{cssClass: expression}"]>dark</button>
    <h1>{{ title }}</h1>
    <router-outlet></router-outlet>
  `,
  styles: [],
})
export class AppComponent {
  title = 'My Profile';
  theme: Boolean = true;
  toggleTheme(){
    this.theme = !this.theme;
  }
}
