import { Component, OnInit } from '@angular/core';
@Component({
  selector: 'app-root',
  template: `
    <button (click)="toggleTheme" label="change" [ngClass]="{ button: theme }" ]>
      dark
    </button>
    <h1 [ngClass]="{ h1: theme }" ]>{{ title }}</h1>
    <router-outlet></router-outlet>
  `,
  styles: [],
})
export class AppComponent {
  title = 'My Profile';
  theme: Boolean = true;
  toggleTheme() {
    this.theme = !this.theme;
  }
}
