import {
  animate,
  state,
  style,
  transition,
  trigger,
} from '@angular/animations';
import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component } from '@angular/core';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-search-me',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <article class="mlp-25">
      <h2>What are you looking for?</h2>
      <div class="column">
        <button
          (mouseleave)="showImage('')"
          (mouseenter)="showImage('sd')"
          id="sd"
          icon="pi pi-iconName"
          label="label"
        >
          A Software Developer
        </button>
        <button
          (mouseleave)="showImage('')"
          (mouseenter)="showImage('ta')"
          id="ta"
          icon="pi pi-iconName"
          label="label"
        >
          An architectural technician
        </button>
      </div>
      <img [@fadeInOut] class="img-gif" *ngIf="gifImgBool" [src]="gifImgUse" />
    </article>
  `,
  animations: [
    trigger('fadeInOut', [
      state(
        'void',
        style({

        })
      ),
      transition('void <=> *', [
        animate(500), // 500ms de animación
      ]),
    ]),
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SearchMeComponent {
  gifImgBool: boolean = false;
  gifImgUse: string = '';
  showImage(image: string) {
    if (image == 'sd') {
      this.gifImgBool = true;
      this.gifImgUse = 'src/assets/img/software-developer.gif';
    } else if (image == 'ta') {
      this.gifImgBool = true;
      this.gifImgUse = 'src/assets/img/architectural-technician.gif';
    } else {
      this.gifImgBool = false;
    }
  }
}
